import React from 'react'
import { Calendar, Views, dateFnsLocalizer } from 'react-big-calendar'
import events from './events'
import ExampleControlSlot from './ControlSlot'
import _ from 'lodash'
import { format } from "date-fns";
import moment from 'moment'
import 'moment/locale/nb';
import 'react-big-calendar/lib/css/react-big-calendar.css';
import parse from 'date-fns/parse'
import startOfWeek from 'date-fns/startOfWeek'
import getDay from 'date-fns/getDay'
import enUS from 'date-fns/locale/en-US'
import SchedularForm from './components/SchedularForm';


const propTypes = {}

class CreateEventWithNoOverlap extends React.Component {
  constructor(...args) {
    super(...args)

    this.state = {
      events: _.cloneDeep(events),
      dayLayoutAlgorithm: 'no-overlap',
      starttimefull:'',
      endtimefull:'',
      titleful:'',
      showform:false,
    }
  }

  displayForm() {
    this.setState({
        showform:true
    })

}

  handleSelect = ({ start, end }) => {
    const title = window.prompt('New Event name')
    if (title);

      this.setState({
        events: [
          ...this.state.events,
          {
            start,
            end,
            title,
          },
        ],
        starttimefull: start,
        endtimefull: end,
        titleful:title
      })
  }

  render() {

    const locales = {
        'en-US': enUS,
      }

      const localizer = dateFnsLocalizer({
        format,
        parse,
        startOfWeek,
        getDay,
        locales,
      })
    return (
      <div style={{height:'90vh', float:'left', width:'100%'}}>
                  <div>
                  <div className="addmorebtn-toggle" onClick={this.props.toggleAandR}>
                        {(this.props.selectVisible) ? 

                        <div className="addmorebtn-toggle-inner-1" style={{backgroundColor:'tomato'}}> 
                            <p style={{color:'#fff'}}><FaTimes/></p>
                            <p style={{color:'#fff'}}>Close</p>
                        </div>
                        : 
                        <div className="addmorebtn-toggle-inner-2">
                            <p><FaPlus/></p>
                            <p>Add more video</p>
                        </div>
                        }
                </div>
                  </div>
                  <div className="">
                        <span onClick={() => this.displayForm()}> Submit</span>
                    </div>
        <ExampleControlSlot.Entry waitForOutlet>
          <strong>
            Click an event to see more info, or drag the mouse over the calendar
            to select a date/time range.
            <br />
            The events are being arranged by `no-overlap` algorithm.
          </strong>
        </ExampleControlSlot.Entry>
        <Calendar
          selectable
          localizer={localizer}
          events={this.state.events}
          defaultView={Views.WEEK}
          scrollToTime={new Date(2022, 1, 8, 6)}
          defaultDate={new Date(2022, 0, 12)}
          //defaultDate={moment().toDate()}
          onSelectEvent={event => alert(event.title)}
          onSelectSlot={this.handleSelect}
          dayLayoutAlgorithm={this.state.dayLayoutAlgorithm}
        />



                     {(this.state.showform) ? 
                        <SchedularForm 
                           presentTabprop={this.state.presentTab} 

                           starttimeprop={format(this.state.starttimefull, "HH:mm")} 
                           endtimeprop={format(this.state.endtimefull, "HH:mm")}
                           starttimepropfull={this.state.starttimefull} 
                           endtimepropfull={this.state.endtimefull}
                           selectedvideo ={this.state.submittingvideoid}
                           />
                     :

                    <div></div>}
      </div>
    )
  }
}

CreateEventWithNoOverlap.propTypes = propTypes

export default CreateEventWithNoOverlap
