import React, { Component } from 'react';
import { BrowserRouter, Router, Switch, Route, Link } from "react-router-dom";
import Header from './components/Header';
import Sidebar from './components/Sidebar';
import '../assets/css/videos.css'
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import ReactPlayer from 'react-player/lazy'
import 'react-tabs/style/react-tabs.css';
import { FaSearch, FaUser, FaVolumeDown, FaVolumeUp, FaBackward, FaForward, FaPause, FaVimeoSquare, FaPlay, FaBars, FaTimes, FaFile } from 'react-icons/fa'
import "react-datepicker/dist/react-datepicker.css";
import SchedulerTabs from './components/SchedulerTabs'
import { format } from "date-fns";
import TimeRange from "react-timeline-range-slider";
import Card from 'react-bootstrap/Card'
import { uid, suid } from 'rand-token';
import UploadVideoForm from './components/UploadVideoForm'
import CalScheduler from './components/CalScheduler'
import CreateEventWithNoOverlap from './createEvent';






class Videos extends Component {
    constructor() {
        super();
        this.state = {
            showmodal: false,
            showmodal2: false,
            isLoggedin: false,
            useremail: '',
            isLoaded: false,
            videos: [],
            selectedArray: [],
            selectVisiblity: false,
        }
    }

    setShow = (e) =>{
        this.setState({
            showmodal:e
        })
      }

      setShow2 = (e) =>{
        this.setState({
            showmodal2:e,
            showmodal:false
        })
      }

      fetchVideos = () => {
        //http://192.168.8.100/videostreaming/allvideos
        fetch('/videostreaming/alladminvideos.php', {
            method: 'POST',

            body: JSON.stringify({
                accesstoken: 'userName',

            })
        })

            .then((response) => response.json())
            .then((responseJSON) => {
                this.setState({
                    isLoaded: true,
                    videos: responseJSON['output'][0]['data'],
                })
            //  console.log(responseJSON);
            }).catch((error)=>{
                //console.log(error)
            })
    }


    componentDidMount() {
        this.fetchVideos();

        this.interval = setInterval(() => this.fetchScheduledVideos(), 1000);
    }


    componentWillUnmount() {
        clearInterval(this.interval);

    }



    fetchScheduledVideos = () => {
        //http://192.168.137.1/videostreaming/allvideos
        fetch('/videostreaming/scheduledvideos.php', {
            method: 'POST',

            body: JSON.stringify({
                accesstoken: 'iita-web-developers',
                selectedday: this.state.presentTab,
            })
        })

            .then((response) => response.json())
            .then((responseJSON) => {
                this.setState({
                    isLoaded: true,
                    videodata: responseJSON['output'][0]['data'],
                });
                let test = responseJSON;
                console.log(test);
                //console.log(this.state.presentTab);


            })
    }


    toggleAddVideo = () => {
        this.setState((prevState) =>( {
            selectVisiblity : !prevState.selectVisiblity
        }))
    }

    selectTiming(vid, vpersecond) {
        this.setState({
            showtimepicker: true,
            submittingvideoid: vid
        })
        this.toggleAddVideo();
    }


    render() {
        return (
            <div className="body">
                <Sidebar showmodal1={() => this.setShow(true)}/>
                <div className="content" style={{ height:'auto', }}>
                <Header />
                <div className="page_container" >
                   <div className="index_wrapper">

                      <div>
                          <div>
                              <h1>{this.state.selectedArray}</h1>
                          </div>
                       </div>
                       <div>
                  <div className="addmorebtn-toggle" onClick={this.toggleAddVideo} style={{marginBottom:'3vh', cursor:'pointer'}}>

                        <div className="addmorebtn-toggle-inner-2">
                            <p><FaFile/></p>
                            <p>Select Video</p>
                        </div>

                </div>
                  </div>
                       <div>
                    {(this.state.selectVisiblity) ? <div style={{}}>
                        {this.state.videos.map((video) =>
                            <div className="admin_video_box2" key={video.videoid}>
                            <Card className="admin_video_box_img">
                                           <Card.Img src={video.videothumbnail} alt="image" className="admin_index_left_a_img"/>
                                           <div className="admin_videoview_content_wrap"></div>
                                           <Card.ImgOverlay>
                                             <Card.Text>
                                               <div className="admin_overlaycontent-sidebar">
                                                  <Link to={`videoview/${video.videoid}`} className="link"><span><FaPlay/></span></Link>
                                               </div>
                                             </Card.Text>
                                           </Card.ImgOverlay>
                                         </Card>
                            <div className="admin_video_box_content">

                                <div className="admin_video_box_content_right">
                                     <span className="admin_span_title">{video.videotitle}</span>
                                    <div className="admin_video_content_right_c">
                                        <span>Duration: {video.videoduration}</span>
                                        <span  onClick={() => this.selectTiming(video.videoid, video.videopersecond)}>Add</span> 
                                    </div>
                                </div>
                             </div>
                            </div>



                            )}
                    </div> : <div></div>}
                </div>


                       <div>
                    <UploadVideoForm
                     openModal={this.state.showmodal}
                     closeModal={() => this.setShow(false)}
                     openModal2={this.state.showmodal2}
                     opensecondModal={() => this.setShow2(true)}
                     closeModal2={() => this.setShow2(false)}
                     />
                  </div>
                  <div>
                     <CreateEventWithNoOverlap/>
                  </div>




                   </div>
                </div>
                </div>
            </div>
        );



    }
}

export default Videos;
